import styled from "styled-components";

// Container do campo de input da calculadora
export const InputContainer = styled.div`
    width: 100%;
    height: 75px;
    background-color: #AAAAfA;

    display: flex;
    align-items: center;
    justify-content: flex-end;

    font-size: 24px;
    font-family: 'Roboto';


    input{
        width:100%;
        height: 75px;
        background-color: #AAAAFA;
        border: 0;
        padding: 0 10px;
        outline: none;

        font-size: 24px;
        font-family: 'Roboto';
        color: #FFF;
    }
`
