import styled from "styled-components";

export const ButtonContainer = styled.button`
    padding:20px;
    border: 0,2rem solid #000;

    font-size:1.2rem;
    font-weight: 700;

    color:#555;
    flex:1;

    &:hover{
        opacity: 0.6;
    }
`
